import React, { useState } from "react";
import {
  LayoutGrid,
  Monitor,
  Cpu,
  Camera,
  Watch,
  Headphones,
  Bluetooth,
  Cloud,
  Plug,
  ShieldCheck,
  Server,
  HardDrive,
  LampDesk,
  Menu,
  X,
  ChevronLeft,
} from "lucide-react";

const sidebarIcons = [
  { Icon: LayoutGrid, label: "Dashboard" },
  { Icon: Monitor, label: "Monitors" },
  { Icon: Cpu, label: "Processors" },
  { Icon: Camera, label: "Cameras" },
  { Icon: Watch, label: "Watches" },
  { Icon: Headphones, label: "Headphones" },
  { Icon: Bluetooth, label: "Bluetooth" },
  { Icon: Cloud, label: "Cloud" },
  { Icon: Plug, label: "Power" },
  { Icon: ShieldCheck, label: "Security" },
  { Icon: Server, label: "Servers" },
  { Icon: HardDrive, label: "Storage" },
  { Icon: LampDesk, label: "Lighting" },
];

const Sidebar = ({ isOpen, onClose }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <>
      {/* Overlay - only on mobile */}
      {isOpen && (
        <div
          onClick={onClose}
          className="fixed inset-0 bg-black/50 z-40 lg:hidden"
        />
      )}

      {/* Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-full 
          bg-white border-r border-[#d8dee4]
          z-50 transition-all duration-300 ease-in-out
          ${isOpen ? "w-64" : "w-16"}
          ${isOpen ? "translate-x-0" : "-translate-x-full lg:translate-x-0"}
        `}
      >
        {/* Toggle Button - Desktop */}
        <div className="hidden lg:flex justify-end p-2">
          <button
            onClick={() => onClose?.()}
            className="
              w-8 h-8
              flex items-center justify-center
              rounded-full
              text-[#425A8B]
              hover:bg-[#F2F4F7]
              transition
            "
          >
            <ChevronLeft 
              size={20} 
              className={`transition-transform ${isOpen ? "" : "rotate-180"}`}
            />
          </button>
        </div>

        {/* Mobile Close Button */}
        <div className="flex lg:hidden justify-end p-4">
          <button
            onClick={onClose}
            className="
              w-8 h-8
              flex items-center justify-center
              rounded-full
              text-[#425A8B]
              hover:bg-[#F2F4F7]
              transition
            "
          >
            <X size={20} />
          </button>
        </div>

        {/* Sidebar Content */}
        <div className="flex flex-col items-center py-4 space-y-2 px-2">
          {sidebarIcons.map((item, index) => (
            <button
              key={index}
              onClick={() => setActiveIndex(index)}
              className={`
                w-full
                flex items-center
                rounded-lg
                py-3 px-3
                transition-all duration-200
                ${activeIndex === index 
                  ? "bg-[#425A8B] text-white" 
                  : "text-[#425A8B] hover:bg-[#F2F4F7]"
                }
              `}
            >
              <div className="flex items-center justify-center w-8">
                <item.Icon size={20} />
              </div>
              
              {/* Label - only visible when expanded */}
              {isOpen && (
                <span className="ml-3 text-sm font-medium whitespace-nowrap opacity-0 animate-fadeIn">
                  {item.label}
                </span>
              )}
            </button>
          ))}
        </div>
      </aside>
    </>
  );
};

// Usage Example Component
export const AppWithDrawer = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Main App Header with Toggle Button */}
      <header className="bg-white shadow-sm p-4">
        <div className="container mx-auto flex items-center justify-between">
          <button
            onClick={toggleSidebar}
            className="lg:hidden p-2 rounded-lg hover:bg-gray-100"
          >
            <Menu size={24} />
          </button>
          
          <h1 className="text-xl font-semibold">Dashboard</h1>
          
          <div className="hidden lg:flex items-center space-x-4">
            <span className="text-sm text-gray-600">Collapsed: {!isSidebarOpen ? "Yes" : "No"}</span>
            <button
              onClick={toggleSidebar}
              className="px-4 py-2 bg-[#425A8B] text-white rounded-lg hover:bg-[#344a6e] transition"
            >
              {isSidebarOpen ? "Collapse" : "Expand"} Sidebar
            </button>
          </div>
        </div>
      </header>

      {/* Main Layout */}
      <div className="flex">
        <Sidebar 
          isOpen={isSidebarOpen} 
          onClose={() => setIsSidebarOpen(false)} 
        />
        
        {/* Main Content */}
        <main className={`
          flex-1 p-4 lg:p-6
          transition-all duration-300
          ${isSidebarOpen ? "lg:ml-64" : "lg:ml-16"}
        `}>
          <div className="bg-white rounded-lg shadow p-6">
            <h2 className="text-2xl font-bold mb-4">Main Content</h2>
            <p className="text-gray-600">
              This content area adjusts based on sidebar state.
              When sidebar is expanded, content moves to the right.
              When collapsed, it expands to fill the space.
            </p>
            
            <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="bg-blue-50 p-4 rounded-lg">
                <h3 className="font-semibold">Card 1</h3>
                <p className="text-sm text-gray-600 mt-2">Some content here</p>
              </div>
              <div className="bg-green-50 p-4 rounded-lg">
                <h3 className="font-semibold">Card 2</h3>
                <p className="text-sm text-gray-600 mt-2">Some content here</p>
              </div>
              <div className="bg-purple-50 p-4 rounded-lg">
                <h3 className="font-semibold">Card 3</h3>
                <p className="text-sm text-gray-600 mt-2">Some content here</p>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

// Add this CSS for fade-in animation
const styles = `
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}
.animate-fadeIn {
  animation: fadeIn 0.3s ease-in-out forwards;
}
`;

// Inject styles
if (typeof document !== 'undefined') {
  const styleSheet = document.createElement("style");
  styleSheet.textContent = styles;
  document.head.appendChild(styleSheet);
}

export default Sidebar;
