import React, { useState } from "react";
import {
  LayoutGrid,
  Monitor,
  Cpu,
  Camera,
  Watch,
  Headphones,
  Bluetooth,
  Cloud,
  Plug,
  ShieldCheck,
  Server,
  HardDrive,
  LampDesk,
  X,
  ChevronLeft,
} from "lucide-react";

const sidebarIcons = [
  { Icon: LayoutGrid, label: "Dashboard" },
  { Icon: Monitor, label: "Monitors" },
  { Icon: Cpu, label: "Processors" },
  { Icon: Camera, label: "Cameras" },
  { Icon: Watch, label: "Watches" },
  { Icon: Headphones, label: "Headphones" },
  { Icon: Bluetooth, label: "Bluetooth" },
  { Icon: Cloud, label: "Cloud" },
  { Icon: Plug, label: "Power" },
  { Icon: ShieldCheck, label: "Security" },
  { Icon: Server, label: "Servers" },
  { Icon: HardDrive, label: "Storage" },
  { Icon: LampDesk, label: "Lighting" },
];

const Sidebar = ({ isOpen, onClose }) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [expanded, setExpanded] = useState(true);

  const toggleExpand = () => setExpanded(!expanded);

  return (
    <>
      {/* Overlay for mobile */}
      {isOpen && (
        <div
          onClick={onClose}
          className="fixed inset-0 bg-black/50 z-40 lg:hidden"
        />
      )}

      {/* Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-full 
          bg-white border-r border-[#d8dee4]
          z-50 transition-all duration-300 ease-in-out
          ${expanded ? "w-64" : "w-16"}
          ${isOpen ? "translate-x-0" : "-translate-x-full lg:translate-x-0"}
        `}
      >
        {/* Desktop Toggle Button */}
        <div className="hidden lg:flex justify-end p-2">
          <button
            onClick={toggleExpand}
            className="
              w-8 h-8
              flex items-center justify-center
              rounded-full
              text-[#425A8B]
              hover:bg-[#F2F4F7]
              transition
            "
          >
            <ChevronLeft 
              size={20} 
              className={`transition-transform ${expanded ? "" : "rotate-180"}`}
            />
          </button>
        </div>

        {/* Mobile Close Button */}
        <div className="flex lg:hidden justify-end p-4">
          <button
            onClick={onClose}
            className="
              w-8 h-8
              flex items-center justify-center
              rounded-full
              text-[#425A8B]
              hover:bg-[#F2F4F7]
              transition
            "
          >
            <X size={20} />
          </button>
        </div>

        {/* Sidebar Content */}
        <div className="flex flex-col items-center py-4 space-y-2 px-2">
          {sidebarIcons.map((item, index) => (
            <button
              key={index}
              onClick={() => {
                setActiveIndex(index);
                onClose(); // close sidebar on mobile when clicked
              }}
              className={`
                w-full
                flex items-center
                rounded-lg
                py-3 px-3
                transition-all duration-200
                ${activeIndex === index 
                  ? "bg-[#425A8B] text-white" 
                  : "text-[#425A8B] hover:bg-[#F2F4F7]"
                }
              `}
            >
              <div className="flex items-center justify-center w-8">
                <item.Icon size={20} />
              </div>
              
              {expanded && (
                <span className="ml-3 text-sm font-medium whitespace-nowrap">
                  {item.label}
                </span>
              )}
            </button>
          ))}
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
