// components/Sidebar/Sidebar.js
import React, { useState } from "react";
import {
  LayoutGrid,
  Monitor,
  Cpu,
  Camera,
  Watch,
  Headphones,
  Bluetooth,
  Cloud,
  Plug,
  ShieldCheck,
  Server,
  HardDrive,
  LampDesk,
  X,
  ChevronLeft,
} from "lucide-react";

const sidebarIcons = [
  { Icon: LayoutGrid, label: "Dashboard" },
  { Icon: Monitor, label: "Monitors" },
  { Icon: Cpu, label: "Processors" },
  { Icon: Camera, label: "Cameras" },
  { Icon: Watch, label: "Watches" },
  { Icon: Headphones, label: "Headphones" },
  { Icon: Bluetooth, label: "Bluetooth" },
  { Icon: Cloud, label: "Cloud" },
  { Icon: Plug, label: "Power" },
  { Icon: ShieldCheck, label: "Security" },
  { Icon: Server, label: "Servers" },
  { Icon: HardDrive, label: "Storage" },
  { Icon: LampDesk, label: "Lighting" },
];

const Sidebar = ({ isOpen, onClose, expanded = true, onToggleExpand }) => {
  const [activeIndex, setActiveIndex] = useState(0);

  return (
    <>
      {/* Overlay for mobile */}
      {isOpen && (
        <div
          onClick={onClose}
          className="fixed inset-0 bg-black/50 z-40 lg:hidden transition-opacity duration-300"
        />
      )}

      {/* Full Height Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-screen
          bg-white border-r border-[#d8dee4]
          shadow-lg
          z-50 transition-all duration-300 ease-in-out
          ${expanded ? "w-64" : "w-16"}
          ${isOpen ? "translate-x-0" : "-translate-x-full lg:translate-x-0"}
        `}
      >
        {/* Top Section - Toggle & Close */}
        <div className="flex justify-between items-center p-4 border-b border-[#d8dee4] h-16">
          {/* Logo when expanded */}
          {expanded && (
            <div className="flex items-center">
              <div className="h-8 w-8 bg-[#425A8B] rounded-md mr-2"></div>
              <span className="font-semibold text-[#425A8B]">Dhaka Agro</span>
            </div>
          )}
          
          {/* Collapsed logo */}
          {!expanded && (
            <div className="h-8 w-8 bg-[#425A8B] rounded-md mx-auto"></div>
          )}
          
          <div className="flex items-center gap-2">
            {/* Desktop Toggle Button */}
            <button
              onClick={onToggleExpand}
              className="
                w-8 h-8
                flex items-center justify-center
                rounded-full
                text-[#425A8B]
                hover:bg-[#F2F4F7]
                transition
                hidden lg:flex
              "
            >
              <ChevronLeft 
                size={20} 
                className={`transition-transform ${expanded ? "" : "rotate-180"}`}
              />
            </button>

            {/* Mobile Close Button */}
            <button
              onClick={onClose}
              className="
                w-8 h-8
                flex items-center justify-center
                rounded-full
                text-[#425A8B]
                hover:bg-[#F2F4F7]
                transition
                lg:hidden
              "
            >
              <X size={20} />
            </button>
          </div>
        </div>

        {/* Sidebar Navigation - Scrollable */}
        <div className="flex-1 overflow-y-auto py-4">
          <div className="space-y-1 px-2">
            {sidebarIcons.map((item, index) => (
              <button
                key={index}
                onClick={() => {
                  setActiveIndex(index);
                  onClose(); // Close sidebar on mobile when clicked
                }}
                className={`
                  w-full
                  flex items-center
                  rounded-lg
                  py-3 px-3
                  transition-all duration-200
                  group
                  ${activeIndex === index 
                    ? "bg-[#425A8B] text-white shadow-sm" 
                    : "text-[#425A8B] hover:bg-[#F2F4F7] hover:shadow-sm"
                  }
                `}
              >
                <div className={`flex items-center justify-center w-8 ${activeIndex === index ? 'text-white' : 'text-[#425A8B]'}`}>
                  <item.Icon size={20} />
                </div>
                
                {expanded && (
                  <span className="ml-3 text-sm font-medium whitespace-nowrap">
                    {item.label}
                  </span>
                )}
                
                {/* Tooltip for collapsed state */}
                {!expanded && (
                  <div className="
                    absolute left-full ml-2 px-2 py-1
                    bg-gray-900 text-white text-xs rounded
                    opacity-0 group-hover:opacity-100
                    transition-opacity duration-200
                    pointer-events-none
                    z-50
                    whitespace-nowrap
                  ">
                    {item.label}
                  </div>
                )}
              </button>
            ))}
          </div>
        </div>

        {/* Bottom Section - User Profile */}
        <div className="p-4 border-t border-[#d8dee4]">
          <div className={`flex items-center ${expanded ? 'justify-start' : 'justify-center'}`}>
            <div className="h-8 w-8 rounded-full bg-gray-300 flex items-center justify-center">
              <span className="text-sm font-medium text-gray-600">U</span>
            </div>
            {expanded && (
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-700">User Account</p>
                <p className="text-xs text-gray-500">View Profile</p>
              </div>
            )}
          </div>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
