// App.js
import React, { useState, useEffect } from "react";
import Header from "./components/Header/NavBar";
import Sidebar from "./components/Sidebar/Sidebar";
import HeroSection from "./components/HeroSection";
import TopSellingProducts from "./components/TopSellingProducts";
import CategorySection from "./components/CategorySection";
import FeaturedCategories from "./components/FeaturedCategories";
import Footer from "./components/Footer";

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarExpanded, setSidebarExpanded] = useState(true);
  const [isMobile, setIsMobile] = useState(false);

  // Check if mobile view
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 1024);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Handle sidebar close on mobile when clicking outside
  useEffect(() => {
    if (sidebarOpen && isMobile) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'auto';
    }

    return () => {
      document.body.style.overflow = 'auto';
    };
  }, [sidebarOpen, isMobile]);

  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Full Height Sidebar */}
      <div className={`
        fixed lg:static
        h-screen
        z-50
        transition-all duration-300 ease-in-out
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        ${sidebarExpanded ? 'w-sidebar-expanded' : 'w-sidebar-collapsed'}
        lg:translate-x-0
        lg:w-${sidebarExpanded ? 'sidebar-expanded' : 'sidebar-collapsed'}
      `}>
        <Sidebar
          isOpen={sidebarOpen}
          onClose={() => setSidebarOpen(false)}
          expanded={sidebarExpanded}
          onToggleExpand={() => setSidebarExpanded(!sidebarExpanded)}
        />
      </div>

      {/* Overlay for mobile */}
      {sidebarOpen && isMobile && (
        <div
          className="fixed inset-0 bg-black/50 z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Main Content Area */}
      <div className={`
        flex-1
        min-h-screen
        transition-all duration-300 ease-in-out
        ${sidebarExpanded ? 'lg:ml-sidebar-expanded' : 'lg:ml-sidebar-collapsed'}
        ${sidebarOpen ? 'ml-0' : ''}
        w-full
      `}>
        {/* Full Width Header */}
        <div className="sticky top-0 z-40 w-full">
          <Header
            isAuthenticated={false}
            onMenuClick={() => setSidebarOpen(true)}
            sidebarExpanded={sidebarExpanded}
          />
        </div>

        {/* Main Content - Full Width */}
        <main className="w-full px-4 py-8">
          <div className="max-w-7xl mx-auto">
            <HeroSection />

          </div>
        </main>

        {/* Full Width Footer */}
        <Footer />
      </div>
    </div>
  );
}

export default App;
