// App.js - CSS Variables approach
import React, { useState, useEffect } from "react";
import Header from "./components/Header/NavBar";
import Sidebar from "./components/Sidebar/Sidebar";
import HeroSection from "./components/HeroSection";
import Footer from "./components/Footer";

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarExpanded, setSidebarExpanded] = useState(true);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 1024);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Update CSS custom property based on sidebar state
  useEffect(() => {
    if (!isMobile && sidebarExpanded) {
      document.documentElement.style.setProperty('--sidebar-width', '256px');
    } else if (!isMobile && !sidebarExpanded) {
      document.documentElement.style.setProperty('--sidebar-width', '64px');
    } else {
      document.documentElement.style.setProperty('--sidebar-width', '0px');
    }
  }, [sidebarExpanded, isMobile]);

  return (
    <div className="min-h-screen bg-gray-50">
      <style>{`
        .main-content {
          margin-left: var(--sidebar-width, 0px);
          transition: margin-left 300ms ease-in-out;
        }
        @media (max-width: 1024px) {
          .main-content {
            margin-left: 0 !important;
          }
        }
      `}</style>

      {/* Mobile Overlay */}
      {sidebarOpen && isMobile && (
        <div
          className="fixed inset-0 bg-black/50 z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div className={`
        fixed lg:relative
        h-screen
        z-50
        transition-all duration-300
        ${sidebarExpanded ? "w-64" : "w-16"}
        ${sidebarOpen ? "translate-x-0" : "-translate-x-full"}
        lg:translate-x-0
      `}>
        <Sidebar
          isOpen={sidebarOpen}
          onClose={() => setSidebarOpen(false)}
          expanded={sidebarExpanded}
          onToggleExpand={() => setSidebarExpanded(!sidebarExpanded)}
        />
      </div>

      {/* Main Content Area */}
      <div className="main-content min-h-screen">
        {/* Sticky Header */}
        <div className="sticky top-0 z-30">
          <Header
            isAuthenticated={false}
            onMenuClick={() => setSidebarOpen(true)}
            sidebarExpanded={sidebarExpanded}
          />
        </div>


      </div>
    </div>
  );
}

export default App;
