// components/FeaturedCategories.js
const FeaturedCategories = () => {
  return (
    <section className="flex flex-wrap w-full max-h-screen bg-white justify-center item-center p-20">
      <div className="flex w-full lg:w-1/2 fon-sans-serif justify-center items-center 2xl:ml-40 ml-12">
        <h1 className="text-3xl lg:text-5xl font-bold text-[#425a8b] leading-tight">
          Featured Categories
          <div className="text-sm lg:text-2xl font-normal text-[#425a8b 60%] leading-tight pt-3 pb-5">
            Choose your necessary product from this feature categories
          </div>
        </h1>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 container bg-white gap-20">
        {/* Feature Categories cards would go here */}
        {/* Each card with image, title, subcategories, and View All button */}
      </div>
    </section>
  );
};
