import React from "react";
import {
  LayoutGrid,
  Monitor,
  Cpu,
  Camera,
  Watch,
  Headphones,
  Bluetooth,
  Cloud,
  Plug,
  ShieldCheck,
  Server,
  HardDrive,
  LampDesk,
} from "lucide-react";

const sidebarIcons = [
  LayoutGrid,
  Monitor,
  Cpu,
  Camera,
  Watch,
  Headphones,
  Bluetooth,
  Cloud,
  Plug,
  ShieldCheck,
  Server,
  HardDrive,
  LampDesk,
];

const Sidebar = ({ isOpen, onClose }) => {
  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div
          onClick={onClose}
          className="fixed inset-0 bg-black/30 z-40"
        />
      )}

      {/* Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-full w-[64px]
          bg-white border-r border-[#d8dee4]
          z-50 transform transition-transform duration-300
          ${isOpen ? "translate-x-0" : "-translate-x-full"}
        `}
      >
        <div className="flex flex-col items-center py-4 space-y-4">

          {sidebarIcons.map((Icon, index) => (
            <button
              key={index}
              className="
                w-10 h-10
                flex items-center justify-center
                rounded-lg
                text-[#425A8B]
                hover:bg-[#F2F4F7]
                hover:text-blue-600
                transition
              "
            >
              <Icon size={18} />
            </button>
          ))}

        </div>
      </aside>
    </>
  );
};

export default Sidebar;
