import React, { useState } from 'react';
import ProductCard from './ProductCard';

// Create a sample product if you don't have images yet
const sampleProducts = [
  {
    id: 1,
    name: 'HALAL TURKEY (AROUND 12-13 LB)',
    category: 'Turkey',
    image: 'https://images.unsplash.com/photo-1587590227264-0ac64ce63ce8?w=400&h=400&fit=crop', // Fallback image
    price: 28.07,
    originalPrice: 35.60,
    discount: 21,
    rating: 4.5,
    reviewCount: 65,
    timer: 86400,
    isWishlisted: false
  },
  // Add more sample products
];

const BestSellers = () => {
  const [products] = useState(sampleProducts);

  const handleAddToCart = (product) => {
    console.log('Added to cart:', product);
    // Add your cart logic here
  };

  const handleBuyNow = (product) => {
    console.log('Buy now:', product);
    // Add your buy now logic here
  };

  const handleViewDetails = (product) => {
    console.log('View details:', product);
    // Open modal or navigate to product page
  };

  const handleToggleWishlist = (productId, isWishlisted) => {
    console.log(`Product ${productId} wishlisted: ${isWishlisted}`);
    // Update wishlist state
  };

  return (
    <section className="container mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold text-[#425a8b] mb-4">Best Sellers</h1>
      <p className="text-lg text-[#425a8b]/60 mb-8">Special products in this month</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <ProductCard
            key={product.id}
            product={product}
            onAddToCart={handleAddToCart}
            onBuyNow={handleBuyNow}
            onViewDetails={handleViewDetails}
            onToggleWishlist={handleToggleWishlist}
          />
        ))}
      </div>
    </section>
  );
};

export default BestSellers;
